RFM_SCORING

# Who are the top 10 of our best customers?
RFM_SCORING[RFM_SCORING['RFMScore']=='111'].sort_values('monetary_value', ascending=False).head(10)

RFM_SCORING.groupby(['RFMScore'])['NombreCliente'].aggregate('count').reset_index().sort_values('NombreCliente', ascending=False).head(10)

